#include <iostream>
#include <vector>
#include <string>
#include <unordered_map>
#include <unordered_set>
using namespace std;

class Solution
{
public:
    vector<int> findSubstring(const string& str, const vector<string>& subs)
    {
        vector<int> ret;

        if (str.size() < subs.size() * subs[0].size())
        {
            return ret;
        }

        if (str.size() == subs.size() * subs[0].size())
        {
            string st;
            for (auto s : subs)
            {
                st += s;
            }

            if (st == str)
            {
                ret.push_back(0);
                return ret;
            }
        }

        //1.映射匹配字符串的 hash1
        //int hash1[26] = { 0 };
        unordered_map<string, int> hash1;

        //for (auto ch : sub) hash1[ch - 'a']++;
        for (auto string : subs)
            hash1[string]++;

        //2.映射主串的 hash
        //int hash2[26] = { 0 };
        unordered_map<string, int> hash2;

        //int len = sub.size();
        int len = subs.size();

        for (int index = 0; index < subs[0].size(); index++)
        {
            for (int left = index, right = index, count = 0; right < str.size(); right += subs[0].size())
            {
                //进窗口
                //char in = str[right];
                string in = str.substr(right, subs[0].size());

                //hash2[in - 'a']++;
                hash2[in]++;

                //if (hash2[in - 'a'] <= hash1[in - 'a']) 
                //  count++;//统计有效字符个数
                if (hash2[in] <= hash1[in])
                    count++;//统计有效字符个数

                //出窗口
                //if (right - left + 1 > len)//以前我们是用循环的，由于窗口固定不变，因此可以直接用 if
                if (right - left + subs[0].size() > len * subs[0].size())//以前我们是用循环的，由于窗口固定不变，因此可以直接用 if
                {
                    //char out = str[left++];
                    string out = str.substr(left, subs[0].size());
                    left += subs[0].size();

                    //if (hash2[out - 'a'] <= hash1[out - 'a'])
                    //  count--;
                    if (hash2[out] <= hash1[out])
                        count--;

                    //hash2[out - 'a']--;
                    hash2[out]--;
                }

                //更新结果
                if (count == len)
                    ret.push_back(left);
            }
            hash2.clear();
        }

        //3.返回索引结果
        return ret;
    }
};

